
import React from 'react'
import { useState } from 'react'
import TopAuthorCard from './Components/TopAuthorCard'


function Authors() {
    const [topauthors, setTopAuthors] = useState([
        {
            id: 1,
            title: "J. K. Rowling",
            novel:"young adult, fantasy, crime fiction, including the Harry Potter series",
            image: "data:image/webp;base64,"

        },
        {
            id: 2,
            title: "Sidney Sheldon",
            novel:"Suspense",
            image: "data:image/webp;base64,"

        },
        {
            id: 3,
            title: "Gilbert Patten",
            novel:"Adolescent adventures",
            image: "data:image/webp;base64,"

        }
    ])
    return (
        <main>
            <section className='py-4 container '>
                <h2> Top Authors</h2>
                <div className=' d-flex flex-row gap-5 '>
                    {
                        topauthors.map(topauthor => {
                            return (
                                <TopAuthorCard key={topauthor.id} topauthor={topauthor} />

                            )
                        })

                    }

                </div>

            </section>

        </main>


    )
}
export default Authors
