import React, { useState, useEffect } from 'react';
import BookCard from './Components/BookCard';
import './home.css'

function Home() {
    const [books, setBooks] = useState([
        {
            id:1,
            title:"It Ends With Us",
            category:"Romance/Drama",
            image:"data:image/jpeg;base64,"
          
          
          },
          {
            id:2,
            title:"Harry Potter",
            category:"Action/Drama",
            image:"data:image/jpeg;base64,"
          },
        {
            id: 1,
            title: "The Davinci Code",
            category: "Crime/Drama",
            image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSbCI1hSEiwVNvIC5YmRenMvne0753MyVq-_w&s"
        },
        {
            id:4,
            title:"Narnia",
            category:"Action/Drama",
            image:"data:image/jpeg;base64,"
          }

    ]);

    const [authors, setAuthors] = useState([]);

    useEffect(() => {
        // Fetch authors' details
        fetch('https://www.freetestapi.com/api/v1/authors')
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((data) => {
                setAuthors(data);
            })
            .catch((error) => {
                console.error('Error fetching authors:', error);
            });
    }, []);

    return (
        <main>
            <section className='py-4 container'>
                <h2>Recommended Books</h2>
                <div className='d-flex flex-row gap-5'>
                    {books.map((book) => (
                        <BookCard key={book.id} book={book} />
                    ))}
                </div>
            </section>

            <section className='py-4 container'>
                <h2>Authors</h2>
                <div className='d-flex flex-wrap gap-4'>
                    {authors.map((author) => (
                        <article key={author.id} className='p-3 border rounded'>
                            <img
                                src={author.image}
                                alt={author.name}
                                style={{ width: '100px', height: '100px', objectFit: 'cover' }}
                            />
                            <h3>{author.name}</h3>
                            <p><strong>Birth Year:</strong> {author.birth_year}</p>
                            <p><strong>Notable Works:</strong> {author.notable_works.join(', ')}</p>
                        </article>
                    ))}
                </div>
            </section>
        </main>
    );
}

export default Home;
